INSERT INTO `rd_news` (`id`, `title`, `content`, `addtime`, `listorder`, `thumb`, `author`, `fb_time`, `type`, `is_recommend`, `source`, `view_count`) VALUES (24, '沛然环保(08320)附属获地产公司委聘为顾问', '<p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; line-height: 2em; font-family: \" microsoft=\"\" white-space:=\"\">发布公告，于2017年3月31日，附属沛然环境评估工程顾问已获一名独立第三方(其主要业务涉及地产行业)委聘为顾问，对位于香港中环的四栋楼宇及两座综合大厦(共涉及12座优质商业楼宇)进行整项绿建环评认证。</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; line-height: 2em; font-family: \" microsoft=\"\" white-space:=\"\">公告称，集团将作为顾问及绿建专才，根据绿建环评既有建筑(2.0版)下的综合评估计划对位于中环的该等优质商业楼宇进行认证。筹备及顾问工作已于2017年4月展开。</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; line-height: 2em; font-family: \" microsoft=\"\" white-space:=\"\">沛然环保表示，该委聘将令集团得以利用其技术能力为其绿色建筑认证顾问业务开创新里程碑。</p><p><br/></p>', 1494645143, 1, '/uploads/project_news/2016/05/17/f4f7c19436dfe2756a5ae063d6d990b3.jpg', NULL, 1494645077, 1, 1, NULL, 34);
INSERT INTO `rd_news` (`id`, `title`, `content`, `addtime`, `listorder`, `thumb`, `author`, `fb_time`, `type`, `is_recommend`, `source`, `view_count`) VALUES (23, '韩媒：韩国执政党要求立刻停止“非法部署萨德”', '<p style=\"background: 0px center rgb(255, 255, 255); border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; vertical-align: baseline; font-variant-numeric: inherit; font-stretch: inherit; line-height: 1.8; font-family: &quot;Microsoft YaHei&quot;, 黑体; color: rgb(14, 14, 14); white-space: normal;\">韩国执政党共同民主党将就促进停止部署“萨德”反导系统召开听证会，并要求将“萨德”部署问题交由国会审议，并称应该立刻停止非法部署“萨德”。</p><p style=\"background: 0px center rgb(255, 255, 255); border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; vertical-align: baseline; font-variant-numeric: inherit; font-stretch: inherit; line-height: 1.8; font-family: &quot;Microsoft YaHei&quot;, 黑体; color: rgb(14, 14, 14); white-space: normal;\">韩国《亚洲经济》5月12日报道称，共同民主党“萨德”对策特别委员会12日表示，“新政府已经成立，以国防部为代表的行政部门应该立刻停止非法部署“萨德”。</p><p style=\"background: 0px center rgb(255, 255, 255); border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; vertical-align: baseline; font-variant-numeric: inherit; font-stretch: inherit; line-height: 1.8; font-family: &quot;Microsoft YaHei&quot;, 黑体; color: rgb(14, 14, 14); white-space: normal;\">韩国国会议员、国会外交统一委员长沈载权等“萨德”对策委员会的议员们当天召开记者会，表示“萨德”部署对国家安保有重大影响，而且也会产生巨大的经济费用，必须根据《宪法》的规定，首先经过国会审议同意。</p><p style=\"background: 0px center rgb(255, 255, 255); border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; vertical-align: baseline; font-variant-numeric: inherit; font-stretch: inherit; line-height: 1.8; font-family: &quot;Microsoft YaHei&quot;, 黑体; color: rgb(14, 14, 14); white-space: normal;\">沈载权强调说：“文在寅总统在大选时数次承诺要将‘萨德’部署问题交由国会表决。（我们）将促进召开听证会，就‘萨德’部署程序上的合法性、‘萨德’费用分担是否有秘密协议等问题进行调查。”</p><p style=\"background: 0px center rgb(255, 255, 255); border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; vertical-align: baseline; font-variant-numeric: inherit; font-stretch: inherit; line-height: 1.8; font-family: &quot;Microsoft YaHei&quot;, 黑体; color: rgb(14, 14, 14); white-space: normal;\">韩国国防部在当天召开的例行记者会上，表示正在考虑文在寅大选时作出的关于国防问题的承诺。</p><p style=\"background: 0px center rgb(255, 255, 255); border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; vertical-align: baseline; font-variant-numeric: inherit; font-stretch: inherit; line-height: 1.8; font-family: &quot;Microsoft YaHei&quot;, 黑体; color: rgb(14, 14, 14); white-space: normal;\">发言人文尚均说：“（文在寅）总统是军队统帅，国防部将以统帅的方针为基础执行维护国家安全的任务。”</p><p style=\"background: 0px center rgb(255, 255, 255); border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; vertical-align: baseline; font-variant-numeric: inherit; font-stretch: inherit; line-height: 1.8; font-family: &quot;Microsoft YaHei&quot;, 黑体; color: rgb(14, 14, 14); white-space: normal;\">当被问及“是否已完成了“萨德”装备的引进时”，文尚均回答说：“正在部署中。”</p><p style=\"background: 0px center rgb(255, 255, 255); border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; vertical-align: baseline; font-variant-numeric: inherit; font-stretch: inherit; line-height: 1.8; font-family: &quot;Microsoft YaHei&quot;, 黑体; color: rgb(14, 14, 14); white-space: normal;\">在10日的中国外交部例行记者会上，有记者问，韩国新总统文在寅表示将同中方和美方协商“萨德”问题。中方对此有什么评论？外交部发言人耿爽表示，中方在“萨德”问题上的立场是明确、一贯的。我们希望韩方高度重视中方关切，妥善处理“萨德”问题。（赵衍龙）</p><p><br/></p>', 1482375854, 2, '/uploads/project_news/2016/05/17/f4f7c19436dfe2756a5ae063d6d990b3.jpg', '', 1482375590, 2, 1, NULL, 3);
